export const getServerSideProps = async (ctx: GetServerSidePropsContext) => {
  const helpers = await createHelpers(ctx)

  await Promise.all([helpers.getFeaturedBounties.fetch()])

  return {
    props: {
      trpcState: helpers.dehydrate(),
    },
  }
}
